import React, {useState} from 'react'

import './Expenses.css'
import ExpenseItem from './ExpenseItem';
import ExpensesFilter from './ExpenseFilter';

const Expenses = (props) => {

const [filteredYear, setFilteredYear] = useState('2020');

// Create a copy of the props (the items)
// Filter them by year
// Use that new const in mapping
const filteredExpenses = props.details
.filter(expense => {
  return expense.date.getFullYear().toString() === filteredYear;
});

const filterChangeHandler = selectedYear => {
  // console.log('Expenses.js');
  // console.log(selectedYear);
  setFilteredYear(selectedYear);
}

  return (
   <>

   <div className='expenses'>
   <ExpensesFilter onChangeFilter={filterChangeHandler}
   defaultYear={filteredYear} />

   {filteredExpenses.length === 0 ? (<center><p><font color = 'red'>No expenses found!</font></p></center>): null}

{/* Mappings */}

    {filteredExpenses.map(detail =>
    <div key={detail.id}>
    <ExpenseItem
    title={detail.title} 
    amount={detail.amount}
    date={detail.date}
     /></div>
     )}
      </div>
   </>
  )
}

export default Expenses
