import React, {useState} from 'react'

import './Expenses.css'
import ExpenseItem from './ExpenseItem';
import ExpensesFilter from './ExpenseFilter';

const Expenses = (props) => {

let _expenses = props.details;

const [filteredYear, setFilteredYear] = useState('2020');

const filterChangeHandler = selectedYear => {
  // console.log('Expenses.js');
  // console.log(selectedYear);
  setFilteredYear(selectedYear);
}

  return (
   <>

   <div className='expenses'>
   <ExpensesFilter onChangeFilter={filterChangeHandler}
   defaultYear={filteredYear} />


    <ExpenseItem 
     title={_expenses[0].title} 
     amount={_expenses[0].amount}
     date={_expenses[0].date}
   />

   <ExpenseItem 
     title={_expenses[1].title} 
     amount={_expenses[1].amount}
     date={_expenses[1].date}
   />

  <ExpenseItem 
     title={_expenses[2].title} 
     amount={_expenses[2].amount}
     date={_expenses[2].date}
   />

<ExpenseItem 
     title={_expenses[3].title} 
     amount={_expenses[3].amount}
     date={_expenses[3].date}
   />
      </div>
   </>
  )
}

export default Expenses
